mapper.select:
SELECT id, title, name, type_id, type_name, price, content, writer_email, writer_name, view_cnt, zzim_cnt, reg_date, up_date
FROM danggeun
WHERE id = #{id}

mapper.update:
UPDATE danggeun
SET	view_cnt = view_cnt + 1
WHERE id = #{id}

UPDATE danggeun
SET	zzim_cnt = zzim_cnt + #{cnt}
WHERE id = #{id}
