mapper.select:
SELECT bno, title, content, writer, view_cnt, comment_cnt, reg_date
FROM t_board
WHERE bno = #{bno}

SELECT bno, title, "content", writer, view_cnt, comment_cnt, reg_date 
FROM t_board
ORDER BY reg_date desc, bno desc;

mapper.update:
UPDATE t_board
SET title = #{title} , "content" = #{content}, up_date = now()
WHERE bno = #{bno} AND writer = #{writer}
